mapper.select:
SELECT COUNT(*)
FROM member
WHERE m_email=#{mEmail} and m_pw=#{mPw}

mapper.update:
UPDATE MEMBER 
SET M_NAME=#{mName}, M_PW=#{mPw}, M_BIRTH=#{mBirth}, M_GENDER=#{mGender}, M_GRADE=#{mGrade}
WHERE M_EMAIL=#{mEmail}

mapper.select:
SELECT m_email, m_name, m_pw, m_birth, m_gender, m_grade
FROM member
WHERE m_email = #{mEmail}

SELECT COUNT(*)
FROM member
WHERE M_EMAIL = #{mEmail}

mapper.update:
UPDATE MEMBER 
SET M_GRADE=#{mGrade}
WHERE M_EMAIL=#{mEmail}
